(ns clojure-hackos.extra-long-factorials-test
  "Find the HR test cases at: https://www.hackerrank.com/challenges/extra-long-factorials/problem"
  (:require [clojure.test :as test :refer [deftest testing is]]
            [clojure-hackos.extra-long-factorials :as fact]))

(deftest n!-knows-factorial-of-25
  (testing "Test against first HR test case"
    (is (= "15511210043330985984000000" (fact/n! 25)))))

(deftest n!-knows-factorial-of-45
  (testing "Test against second HR test case"
    (is (= "119622220865480194561963161495657715064383733760000000000" (fact/n! 45)))))

(deftest n!-knows-factorial-of-zero
  (testing "Test handling of zero factorial"
    (is (= "1" (fact/n! 0)))))

(deftest n!-knows-factorial-of-1444
  (testing "Test arbitrary precision limits"
    (is (=
         ""
         (fact/n! 1444)))))
